import React from 'react';
import './Chat.css';
import Message from './components/Message';
import io from 'socket.io-client';

const socket = io.connect('http://localhost:3001', { transports: ['websocket'] });

function Chat() {

  const [name, setName] = React.useState('')
  const [username, setUsername] = React.useState('Tanya');
  const [message, setInputMessage] = React.useState('');

  const [messages, setMessages] = React.useState([]);



  const sendMessage = () => {
    const msg = {
      message,
      username
    };
    setMessages((messages) => [...messages, msg]);
    socket.emit('sendMessage', msg);
    setInputMessage('');
  };

  React.useEffect(() => {
    socket.on('getMessage', (msg) => {
      setMessages((messages) => [...messages, msg]);
    }
  );
  }, []);

  return (
    <div className="chat">
      <header>
        <h1>Super Chat</h1>
      </header>

      <section>
        <div id="change_username">
          <input 
            id="username" 
            type="text" 
            name='message'
            onChange={(e) => setName(e.target.value)}
          />
          <button 
            id="send_username" 
            type="button"
            onClick={() => setUsername(name)}
           >
            Change username
          </button>
        </div>
      </section>

      <section id="chatroom">
        {
          messages.map((item) => (
            <Message username={item.username} message={message.message} />
          ))
        }

      </section>
      <section id="input_zone">
        <input 
          id="message" 
          className="vertical-align" 
          type="text"
          onChange={(e) => setInputMessage(e.target.value)}
          value={message}
        />
        <button 
          id="send_message" 
          className="vertical-align" 
          type="button"
          onClick={sendMessage}
        >
          Send</button>
      </section>
    </div>
  );
}

export default Chat;
