import React from 'react';
import './Chat.css';
import Message from './components/Message';
import io from 'socket.io-client';

const socket = io.connect('http://localhost:3001', { transports: ['websocket'] });

function Chat() {
  const [name, setName] = React.useState('Bob');
  const [nameUser, setNameUser] = React.useState('Bob');

  socket.emit('sendName', name);
  const changeUserName = ()=>{setNameUser(name)}
  return (
    <div className="chat">
      <header>
        <h1>Super Chat</h1>
      </header>
      <section>
        <div id="change_username">
          <input 
           id="username"
           type="text"
           onChange={(e)=>{setName(e.target.value)}}
           value={name}
           />
          <button id="send_username" type="button" onClick={changeUserName}>Change name</button>
        </div>
      </section>

      <section id="chatroom">
        <Message username={nameUser} message="Help me" />
        <Message username={nameUser} message="Help me" />
        <section id="feedback"></section>
      </section>


      <section id="input_zone">
        <input id="message" className="vertical-align" type="text"/>
        <button id="send_message" className="vertical-align" type="button">Send</button>
      </section>
    </div>
  );
}

export default Chat;
